import React, { createContext, useState } from "react";
import Navbar from "./Component/Navbar";
import News from "./Component/News";
import SavedArticles from "./Component/SavedArticles"; // New component for displaying saved articles
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

export const AppContext = createContext();

const App = () => {
  const [savedArticles, setSavedArticles] = useState([]);

  const saveArticle = (article) => {
    if (!savedArticles.some((savedArticle) => savedArticle.url === article.url)) {
      setSavedArticles([...savedArticles, article]);
      alert("Article saved!");
    } else {
      alert("Article already saved!");
    }
  };

  return (
    <AppContext.Provider value={{ savedArticles, saveArticle }}>
      <Router>
        <div>
          <Navbar />
          <Routes>
            <Route
              exact
              path="/"
              element={<News key="general" country="in" category="general" />}
            />
            <Route
              exact
              path="/business"
              element={<News key="business" country="in" category="business" />}
            />
            <Route
              exact
              path="/entertainment"
              element={<News key="entertainment" country="in" category="entertainment" />}
            />
            <Route
              exact
              path="/health"
              element={<News key="health" country="in" category="health" />}
            />
            <Route
              exact
              path="/science"
              element={<News key="science" country="in" category="science" />}
            />
            <Route
              exact
              path="/sports"
              element={<News key="sports" country="in" category="sports" />}
            />
            <Route
              exact
              path="/technology"
              element={<News key="technology" country="in" category="technology" />}
            />
            <Route
              exact
              path="/saved"
              element={<SavedArticles />}
            />
          </Routes>
        </div>
      </Router>
    </AppContext.Provider>
  );
};

export default App;
