import React from "react";

const NewsItem = ({ Title, description, imageurl, newsurl, author, date, source, onSave }) => {
  return (
    <div className="card">
      {/* ... existing code ... */}
      <div className="card-body">
        <h5 className="card-title">{Title}</h5>
        <p className="card-text">{description}</p>
        {/* ... existing code ... */}
        {/* Save button */}
        <button className="btn btn-primary" onClick={onSave} style={{ marginRight: '10px' }}>
          Save
        </button>
        {/* Read More button */}
        <a href={newsurl} target="_blank" rel="noreferrer" className="btn btn-dark">
          Read More
        </a>
      </div>
    </div>
  );
};

export default NewsItem;
