import React, { useContext } from "react";
import { AppContext } from "../App"; // Adjust the path based on your project structure

const SavedArticles = () => {
  const { savedArticles } = useContext(AppContext);

  return (
    <div className="container my-3">
      <h1 style={{ marginTop: "90px" }}>Saved Articles</h1>
      <div className="container my-3">
        <div className="row">
          {savedArticles.map((article) => {
            return (
              <div className="col-sm-4" key={article.url}>
                {/* You can display saved articles similar to NewsItem */}
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">{article.title}</h5>
                    {/* Add other details as needed */}
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default SavedArticles;
